    function set_array_from_csv( $dat, $name, $filename, $len) {
	$row = 0;
	$ary = array();
	if (($handle = fopen($filename, "r")) !== FALSE) {
	    while (($data = fgetcsv($handle, 1000, ",")) !== FALSE) {
		$num = count($data);
		for ($c=0; $c < $num; $c++) {
		    $ary[$row] = doubleval($data[$c]);
		}
		$row++;
	    }
	    fclose($handle);
	}
	if ($row != $len) {
	    echo "number of elements $len not matching file length $row" . PHP_EOL;
	    return;
	}
	sscphp_data_set_array( $dat, $name, $ary);
    }
    function set_matrix_from_csv( $dat, $name, $filename, $nr, $nc) {
