	    while (($data = fgetcsv($handle, 1000, ",")) !== FALSE) {
		$col = count($data);
		if ($col != $nc) {
		    echo "number of elements $col not matching row length $nc for row $row" . PHP_EOL;
		    return;
		}
		$row_ary = array();
	        for ($c=0; $c < $col; $c++) {
            		$row_ary[$i] = doubleval($data[$c]);
            		$i++;
        	}
        	$ary[$row]=$row_ary;
        	$row++;
    	    }
    	fclose($handle);
	}
	if ($row != $nr) {
	    echo "number of rows $nr not matching file length $row" . PHP_EOL;
	    return;
	}
	if ($i != ($nr * $nc)) {
	    echo "number of elements $i not matching file length " . $nr*$nc . PHP_EOL;
	    return;
	}
	sscphp_data_set_matrix( $dat, $name, $ary);
